t.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());
gtag('config', '');

$(document).ready(function() {
    var button = $('.reset-templates-button');
    button.click(function() {
        $('#resetTemplatesMode').val($(this).data('mode'));
        var dialog = $('#resetTemplateConfirmation').modal('show');
        var input = dialog.find('input[type="text"]').val('').focus();
        var dialogForm = dialog.find('form');
        dialogForm.submit(function() {
            if (input.val() == dialog.find('.confirmWord').text()) {
                dialog.modal('hide');
                button.prop('disabled', true).text('Working...');
                $('#resetTemplatesForm').attr('action', '/website/resetTemplate');
                $('#resetTemplatesForm').trigger('submit');
            } else {
                input.val('').focus();
            }
            return false;
        });
        return false;
    });
});
